import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from './contexts/AuthContext';
import './Community.css';

function Community() {
  const [posts, setPosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('new'); // 'new' or 'popular'
  const navigate = useNavigate();
  const [isLoggedIn] = useContext(AuthContext);

  useEffect(() => {
    fetchPosts();
  }, [sortBy, searchTerm]);

  const fetchPosts = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/posts?sort=${sortBy}&search=${searchTerm}`, {
        credentials: 'include'
      });
      if (response.ok) {
        const data = await response.json();
        setPosts(data.posts);
      }
    } catch (error) {
      console.error('Í≤åÏãúÍ∏Ä Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Ìèº Ï†úÏ∂ú Ïãú Ïù¥ÎØ∏ useEffectÍ∞Ä Ïã§ÌñâÎêòÎØÄÎ°ú Ï∂îÍ∞Ä Ìò∏Ï∂ú ÌïÑÏöî ÏóÜÏùå
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };

  return (
    <div className="community-container">
      <div className="community-title-section">
        <div className="title-icon-wrapper">
          <span className="title-icon">üìã</span>
          <h1 className="page-title">Í≤åÏãúÍ∏Ä Î™©Î°ù</h1>
        </div>
        {isLoggedIn &&(
          <button 
            className="write-button"
            onClick={()=> navigate('/community/write')}
          >
            <span>üìù</span> Í∏ÄÏì∞Í∏∞
          </button>
        )}
      </div>

      <div className="search-section">
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Í≤ÄÏÉâÏñ¥ ÏûÖÎ†•"
            className="search-input"
          />
          <button type="submit" className="search-button">
            <span>üîç</span> Í≤ÄÏÉâ
          </button>
        </form>
      </div>

      <div className="sort-section">
        <div className="sort-label">
          <span className="sort-icon">üìå</span>
          <span>Ï†ïÎ†¨ Í∏∞Ï§Ä</span>
        </div>
        <div className="sort-buttons">
          <button
            className={`sort-button ${sortBy === 'new' ? 'active' : ''}`}
            onClick={() => setSortBy('new')}
          >
            <span className="sort-btn-icon">‚è±Ô∏è</span> ÏµúÏã†Ïàú
          </button>
          <button
            className={`sort-button ${sortBy === 'popular' ? 'active' : ''}`}
            onClick={() => setSortBy('popular')}
          >
            <span className="sort-btn-icon">üî•</span> Ïù∏Í∏∞Ïàú
          </button>
        </div>
      </div>

      <div className="table-container">
        <table className="posts-table">
          <thead>
            <tr>
              <th>Î≤àÌò∏</th>
              <th>Ï†úÎ™©</th>
              <th>ÏûëÏÑ±Ïûê</th>
              <th>ÏûëÏÑ±Ïùº</th>
              <th>Ï°∞ÌöåÏàò</th>
              <th>Ï¢ãÏïÑÏöî</th>
            </tr>
          </thead>
          <tbody>
            {posts.length > 0 ? (
              posts.map((post, index) => (
                <tr key={post.id} onClick={() => navigate(`/community/post/${post.id}`)}>
                  <td>{posts.length - index}</td>
                  <td className="post-title-cell">{post.title}</td>
                  <td>{post.name}</td>
                  <td>{formatDate(post.wdate)}</td>
                  <td>{post.view}</td>
                  <td>{post.like_count}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="no-posts">
                  Í≤åÏãúÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Community;
