import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './PostDetail.css';

function PostDetail() {
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [likeCount, setLikeCount] = useState(0);
  const [reportCount, setReportCount] = useState(0);
  const { postId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchPostDetail();
  }, [postId]);

  const fetchPostDetail = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/posts/${postId}`, {
        credentials: 'include'
      });
      if (response.ok) {
        const data = await response.json();
        setPost(data.post);
        setComments(data.comments);
        setLikeCount(data.like_count);
        setReportCount(data.post.report || 0);
      }
    } catch (error) {
      console.error('Í≤åÏãúÍ∏Ä Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const handleLike = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/posts/${postId}/like`, {
        method: 'POST',
        credentials: 'include'
      });
      if (response.ok) {
        const data = await response.json();
        setLikeCount(data.like_count);
      }
    } catch (error) {
      console.error('Ï¢ãÏïÑÏöî Ïã§Ìå®:', error);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const response = await fetch(`http://localhost:5001/api/posts/${postId}`, {
          method: 'DELETE',
          credentials: 'include'
        });
        if (response.ok) {
          navigate('/community');
        }
      } catch (error) {
        console.error('Í≤åÏãúÍ∏Ä ÏÇ≠Ï†ú Ïã§Ìå®:', error);
      }
    }
  };

  const handleReportPost = async () => {
    if (window.confirm('Ïù¥ Í≤åÏãúÍ∏ÄÏùÑ Ïã†Í≥†ÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const response = await fetch(`http://localhost:5001/report/post/${postId}`, {
          method: 'POST',
          credentials: 'include'
        });
        const data = await response.json();
        if (response.ok) {
          alert(data.message);
          setReportCount(prev => prev + 1);
        } else {
          alert(`Ïã†Í≥† Ïã§Ìå®: ${data.message}`);
        }
      } catch (error) {
        alert('Ïã†Í≥† Ï§ë Ïò§Î•ò Î∞úÏÉù');
      }
    }
  };

  const handleReportComment = async (commentId) => {
    if (window.confirm('Ïù¥ ÎåìÍ∏ÄÏùÑ Ïã†Í≥†ÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const response = await fetch(`http://localhost:5001/report/comment/${commentId}`, {
          method: 'POST',
          credentials: 'include'
        });
        const data = await response.json();
        if (response.ok) {
          alert(data.message);
          fetchPostDetail(); // ÎåìÍ∏Ä Ïàò Í∞±Ïã†
        } else {
          alert(`Ïã†Í≥† Ïã§Ìå®: ${data.message}`);
        }
      } catch (error) {
        alert('ÎåìÍ∏Ä Ïã†Í≥† Ï§ë Ïò§Î•ò Î∞úÏÉù');
      }
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5001/api/posts/${postId}/comments`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ content: newComment })
      });
      if (response.ok) {
        setNewComment('');
        fetchPostDetail();
      }
    } catch (error) {
      console.error('ÎåìÍ∏Ä ÏûëÏÑ± Ïã§Ìå®:', error);
    }
  };

  const handleCommentDelete = async (commentId) => {
    if (window.confirm('ÎåìÍ∏ÄÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const response = await fetch(`http://localhost:5001/api/comments/${commentId}`, {
          method: 'DELETE',
          credentials: 'include'
        });
        if (response.ok) {
          fetchPostDetail();
        }
      } catch (error) {
        console.error('ÎåìÍ∏Ä ÏÇ≠Ï†ú Ïã§Ìå®:', error);
      }
    }
  };

  if (!post) return <div>Î°úÎî© Ï§ë...</div>;

  return (
    <div className="post-detail-container">
      <div className="post-header">
        <h2>{post.title}</h2>
        <div className="post-info">
          <span>ÏûëÏÑ±Ïûê: {post.name}</span>
          <span>ÏûëÏÑ±Ïùº: {new Date(post.wdate).toLocaleDateString()}</span>
        </div>
      </div>

      <div className="post-content">
        <pre>{post.content}</pre>
      </div>

      <div className="post-actions">
        <button onClick={handleLike}>‚ù§Ô∏è Ï¢ãÏïÑÏöî ({likeCount})</button>
        {post.is_author && (
          <>
            <button onClick={() => navigate(`/community/edit/${postId}`)}>‚úèÔ∏è ÏàòÏ†ï</button>
            <button onClick={handleDelete}>üóëÔ∏è ÏÇ≠Ï†ú</button>
            <button onClick={handleReportPost}>üö® Ïã†Í≥† ({reportCount})</button>
          </>
        )}
      </div>

      <div className="comments-section">
        <h3>üí¨ ÎåìÍ∏Ä</h3>
        <form onSubmit={handleCommentSubmit}>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="ÎåìÍ∏ÄÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            required
          />
          <button type="submit">ÎåìÍ∏Ä ÏûëÏÑ±</button>
        </form>

        <div className="comments-list">
          {comments.map(comment => (
            <div key={comment.id} className="comment">
              <div className="comment-header">
                <strong>{comment.commenter}</strong>
                <span>{new Date(comment.cdate).toLocaleDateString()}</span>
              </div>
              <p>{comment.content}</p>
              <div className="comment-actions">
                {comment.is_author && (
                  <>
                    <button onClick={() => navigate(`/community/comment/edit/${comment.id}`)}>‚úèÔ∏è ÏàòÏ†ï</button>
                    <button onClick={() => handleCommentDelete(comment.id)}>üóëÔ∏è ÏÇ≠Ï†ú</button>
                    <button onClick={() => handleReportComment(comment.id)}>üö® ÎåìÍ∏Ä Ïã†Í≥† ({comment.report || 0})</button>
                  </>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      <button className="back-button" onClick={() => navigate('/community')}>
        ‚Üê Î™©Î°ùÏúºÎ°ú
      </button>
    </div>
  );
}

export default PostDetail;
