import React, { useState } from 'react';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    id: '',
    password: '',
    password_confirm: '',
    nickname: '',
    email: '',
    name: '',
    verificationCode: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isEmailVerified, setIsEmailVerified] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    setError('');
  };

  /** 코드 인증증
  const handleEmailVerification = async () => {
    try {
      const response = await fetch('http://localhost:5001/send-verification-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: formData.email })
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || '인증 코드 전송 실패');
      }
      
      setSuccess('인증 코드가 이메일로 전송되었습니다.');
    } catch (error) {
      setError(error.message);
    }
  };
  

  const handleVerifyCode = async () => {
    try {
      const response = await fetch('http://localhost:5001/verify-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          code: formData.verificationCode
        })
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || '인증 코드가 일치하지 않습니다.');
      }
      
      setIsEmailVerified(true);
      setSuccess('이메일 인증이 완료되었습니다.');
    } catch (error) {
      setError(error.message);
    }
  };
  */

  const validateForm = () => {
    // 모든 필드가 채워져 있는지 확인
    if (!formData.id || !formData.password || !formData.password_confirm || 
        !formData.nickname || !formData.email || !formData.name) {
      setError('모든 필드를 입력해주세요.');
      return false;
    }

    // 비밀번호 일치 확인
    if (formData.password !== formData.password_confirm) {
      setError('비밀번호가 일치하지 않습니다.');
      return false;
    }

    // 아이디 길이 확인
    if (formData.id.length < 3) {
      setError('아이디는 3글자 이상이어야 합니다.');
      return false;
    }

    // 비밀번호 길이 확인
    if (formData.password.length < 4) {
      setError('비밀번호는 4글자 이상이어야 합니다.');
      return false;
    }

    // 이메일 인증 확인
    if (!isEmailVerified) {
      setError('이메일 인증이 필요합니다.');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    try {
      const response = await fetch('http://localhost:5001/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || '회원가입 실패');
      }
      
      setSuccess('회원가입에 성공했습니다!');
      // 로그인 페이지로 리다이렉트
      setTimeout(() => {
        window.location.href = '/login';
      }, 2000);
      
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="container">
      <h1>Sign up</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <label htmlFor="id">아이디 (3글자 이상):</label>
          <input
            type="text"
            id="id"
            name="id"
            value={formData.id}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <label htmlFor="password">비밀번호 (4글자 이상):</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <label htmlFor="password_confirm">비밀번호 확인:</label>
          <input
            type="password"
            id="password_confirm"
            name="password_confirm"
            value={formData.password_confirm}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <label htmlFor="nickname">닉네임:</label>
          <input
            type="text"
            id="nickname"
            name="nickname"
            value={formData.nickname}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <label htmlFor="email">이메일:</label>
          <div className="email-verification">
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              disabled={isEmailVerified}
            />
            {!isEmailVerified && (
              <button 
                type="button" 
                //onClick={handleEmailVerification}
                className="verification-button"
              >
                코드인증
              </button>
            )}
          </div>
        </div>

        {!isEmailVerified && (
          <div className="form-row verification-code">
            <label htmlFor="verificationCode">인증 코드:</label>
            <div className="verification-input">
              <input
                type="text"
                id="verificationCode"
                name="verificationCode"
                value={formData.verificationCode}
                onChange={handleChange}
                required
              />
              <button 
                type="button" 
                //onClick={handleVerifyCode}
                className="verify-button"
              >
                확인
              </button>
            </div>
          </div>
        )}

        <div className="form-row">
          <label htmlFor="name">이름:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <button type="submit">회원가입</button>
      </form>
    </div>
  );
};

export default Register;
